#include <fcntl.h>
#include <stdio.h>
#include <string.h>
#include <sys/stat.h>

int mkstemp(char *template)
{
		if (_mktemp_s(template, strlen(template) + 1) != 0)
				return -1;
		return open(template, _O_CREAT | _O_EXCL, _S_IWRITE);
}
