// Test deoptimization after a loop.
function f(p) {
  var y=0;
  for (var x=0; x<10; x++) {
    if (x > 5) { y=y+p; break;}
  }
  return y+x;
}

for (var i=0; i<100000; i++) f(42);

var result = f("foo");
assertEquals("0foo6", result);
